const RecipeScreen = () => {
  const navigation = useNavigation();

  const renderRecipe = ({ item }) => (
    <TouchableOpacity onPress={() => navigation.navigate('RecipeDetail', { recipe: item })}>
      <View style={styles.card}>
        <Image source={{ uri: item.image }} style={styles.image} />
        <View style={styles.info}>
          <Text style={styles.title}>{item.title}</Text>
          <Text style={styles.description}>{item.description}</Text>
          <View style={styles.macros}>
            <Text style={styles.macro}>Protein: {item.protein}</Text>
            <Text style={styles.macro}>Carbs: {item.carbs}</Text>
            <Text style={styles.macro}>Fats: {item.fats}</Text>
          </View>
        </View>
      </View>
    </TouchableOpacity>
  );

  return (
    <ScrollView style={styles.container}>
      <Text style={styles.heading}>Gym-Focused Recipes</Text>
      <FlatList
        data={gymRecipes}
        renderItem={renderRecipe}
        keyExtractor={(item) => item.id.toString()}
        scrollEnabled={false}
        contentContainerStyle={styles.list}
      />
    </ScrollView>
  );
};

